import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './PaymentRedirect.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

const PaymentRedirect = () => {
  const { paymentId } = useParams();
  const navigate = useNavigate();
  const [status, setStatus] = useState('checking'); // checking, redirecting, error
  const [message, setMessage] = useState('Проверяем платёжную систему...');

  useEffect(() => {
    checkAndRedirect();
  }, [paymentId]);

  const checkAndRedirect = async () => {
    try {
      // Проверяем платёж через API
      const response = await axios.get(`${API_URL}/api/payment/check/${paymentId}`);
      const data = response.data;

      if (data.success && data.available && data.target_url) {
        // ✅ Система работает - редиректим на СБП
        setStatus('redirecting');
        setMessage('Перенаправляем на оплату...');

        // Небольшая задержка для плавности
        setTimeout(() => {
          window.location.href = data.target_url;
        }, 500);
      } else {
        // ❌ Система недоступна - показываем ошибку
        navigate(`/payment-error?type=${data.reason}&message=${encodeURIComponent(data.message)}`);
      }
    } catch (error) {
      console.error('Payment check error:', error);

      // Ошибка запроса - показываем ошибку
      const errorMessage = error.response?.data?.message || 'Ошибка при проверке платежа';
      navigate(`/payment-error?type=error&message=${encodeURIComponent(errorMessage)}`);
    }
  };

  return (
    <div className="payment-redirect-page">
      <div className="redirect-container">
        <div className="spinner"></div>
        <h2>{message}</h2>
        <p className="redirect-hint">
          {status === 'checking' && 'Это займёт всего пару секунд...'}
          {status === 'redirecting' && 'Не закрывайте это окно...'}
        </p>
      </div>
    </div>
  );
};

export default PaymentRedirect;
